{
  "GetHotelDetailsRQ": {
    "SearchCriteria": {
      "HotelRefs": {
        "HotelRef": {
          "HotelCode": "{{hotel_code}}",
          "CodeContext": "GLOBAL"
        }
      },
      "RateInfoRef": {
        "CurrencyCode": "USD",
        
        "PrepaidQualifier": "IncludePrepaid",
        "StayDateRange": {
          "StartDate": "{{start_date}}",
          "EndDate": "{{end_date}}"
        },
        "Rooms": {
          "Room": [
            {
              "Index": 1,
              "Adults": 1,
              "Children": 1,
              "ChildAges": "1"
            }
          ]
        },
        "RatePlanCandidates": {
          "ExactMatchOnly": false,
          "RatePlanCandidate": [
            {
              
            }
          ]
        },
      "InfoSource": "100"
      },
      "HotelContentRef": {
        "DescriptiveInfoRef": {
          "PropertyInfo": true,
          "LocationInfo": true,
          "Amenities": true,
          "Descriptions": {
            "Description": [
              {
                "Type": "ShortDescription"
              },
              {
                "Type": "Alerts"
              },
              {
                "Type": "Dining"
              },
              {
                "Type": "Facilities"
              },
              {
                "Type": "Recreation"
              },
              {
                "Type": "Services"
              },
              {
                "Type": "Attractions"
              },
              {
                "Type": "CancellationPolicy"
              },
              {
                "Type": "DepositPolicy"
              },
              {
                "Type": "Directions"
              },
              {
                "Type": "Policies"
              },
              {
                "Type": "SafetyInfo"
              },
              {
                "Type": "TransportationInfo"
              }
            ]
          },
          "AdditionalCharges": true,
          "Airports": true,
          "AcceptedCreditCards": true,
          "GuaranteePolicies": true
        },
        "MediaRef": {
          "MaxItems": "ALL",
          "MediaTypes": {
            "Images": {
              "Image": [
                {
                  "Type": "ORIGINAL"
                }
              ]
            }
          },
          "AdditionalInfo": {
            "Info": [
              {
                "Type": "CAPTION",
                "value": true
              }
            ]
          },
          "Languages": {
            "Language": [
              {
                "Code": "EN"
              },
              {
                "Code": "ES"
              },
              {
                "Code": "IT"
              }
            ]
          }
        }
      }
    }
  }
}
